<?php/** Classes representing one to one relationship between * Sales table of an e-commerce store and that of customers, * store, and item * By Paul Ezaga*/// Sales Class to represent the sales tableclass Sales{	// Variables that whole references to adjoined tables	 private $_store;	 private $_customer;	 private $_item;	 private $quantity;	 private $price;	 private $_dbase;		//  Class Constructor function 	 public function __construct()	 {		 $this->_store = new Store();		 $this->_customer = new Customer();		 $this->_item = new Item(); 		 $this->_dbase = Connector::doConnect();		 	 }	 	 //Getter method to get the referencing StoreId	 public function getStoreId()	 {		 		return $this->_store->getStoreId(); 		 	 }		//Setter method to Set the referencing StoreId	public function setStoreId($storeId)	{				$this->_store->setStoreId($storeId);		 	}		//Getter method to get the referencing CustomerId	public function getCustomerId()	{		 		return $this->_customer->getCustomerId(); 		 	}		//Setter method to Set the referencing CustomerId	public function setCustomerId($customerId)	{		 		 $this->_store->setCustomerId($customerId);		 	}		//Getter method to get the referencing ItemId	public function getItemId()	{		 		 return $this->_item->getItemId(); 		 	}		//Setter method to Set the referencing ItemId	public function setItemId($itemId)	{		 		 $this->_item->setItemId($itemId);		 	}	}	// Customer Classclass Customer{	// Private Table variables	private $_customerId;	private $_name;	private $_address;		//Getter method to get the referencing CustomerId	public function getCustomerId()	{		 return $this->_customerId; 	 	}		//Setter method to set the CustomerItemId	public function setCustomerId($customerId)	{	 	 $this->_customerId = $customerId;	 	}		}	// Item Class or Productclass Item{	private $_itemId;	private $_category;	private $_brand;	private $_color;		//Getter method to get the ItemId	public function getItemId()	{	 	 return $this->_itemId; 	 	}		//Getter method to get the ItemId	public function setItemId($itemId)	{	 	 $this->_itemId = $itemId;	 	}		}		// Store Class where item was purchasedclass Store{	private $_storeId;	private $_city;	private $_state;		// Getters Method	public function getStoreId()	{	 	 return $this->_storeId; 	 	}		// Setter Method	public function setStoreId($storeId)	{	 	 $this->_storeId = $storeId;	 	}		}				?>																					}